import React, { useEffect, useState } from 'react';
import axios from 'axios';
import HealthTrends from './HealthTrends'; // Ensure this path is correct

const Dashboard: React.FC = () => {
    const [healthInsights, setHealthInsights] = useState<any[]>([]);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchHealthInsights = async () => {
            try {
                const response = await axios.get('http://localhost:5001/api/health-insights', {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`, // Assuming you're storing the JWT in localStorage
                    },
                });
                setHealthInsights(response.data);
            } catch (err) {
                setError('Failed to fetch health insights');
                console.error(err);
            } finally {
                setLoading(false);
            }
        };

        fetchHealthInsights();
    }, []);

    if (loading) {
        return <div className="text-center p-4">Loading...</div>;
    }

    if (error) {
        return <div className="text-center p-4 text-red-500">{error}</div>;
    }

    // Assuming healthInsights contains data for visualization
    const data = healthInsights.map((insight) => insight.value); // Adjust according to your data structure
    const labels = healthInsights.map((insight) => insight.date); // Adjust according to your data structure

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-2xl font-bold mb-4">Your Health Insights</h1>

            {/* Visualization Section */}
            <div className="bg-white p-4 rounded shadow mb-6">
                <HealthTrends data={data} labels={labels} />
            </div>

            {/* Cards Section */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                <div className="bg-white p-4 rounded shadow">
                    <h2 className="font-semibold text-lg">Card 1</h2>
                    <p>Details for card 1.</p>
                </div>
                <div className="bg-white p-4 rounded shadow">
                    <h2 className="font-semibold text-lg">Card 2</h2>
                    <p>Details for card 2.</p>
                </div>
                <div className="bg-white p-4 rounded shadow">
                    <h2 className="font-semibold text-lg">Card 3</h2>
                    <p>Details for card 3.</p>
                </div>
                {/* Add more cards as needed */}
            </div>
        </div>
    );
};

export default Dashboard;
